import React, { Component } from 'react';

class Index extends Component {
    constructor (props) {
        super(props)
        this.state = {
            count: 0
        }
    }
    _handleClick () {
        this.setState ({
            count: this.state.count
        })
    }
    render () {
        return (
            <div>
                this is home <br/>
                当前计数： { this.state.count}
                <button onClick={ () => this._handleClick()} ></button>
            </div>
        )
    }
}
export default Index
