import { INCREMENT, DECREMENT, RESET } from 'actions/counter';

/**
 * 初始化state 
 */

const initState = {
    count: 0
}

const reducer = ( state = { count: 0}, action) => {
    switch (action.type) {
        case INCREMENT:
            return {
                count: ++state.count
            }
        case DECREMENT:
            return {
                count: --state.count
            }
        case RESET:
            return {
                count: 0
            }
        default:
            return false
    }
}
export default reducer
